<?php 	$homeland_all_agents = get_option( 'homeland_all_agents' );	$homeland_about_agents = get_option( 'homeland_about_agents' );	$homeland_agent_order = get_option( 'homeland_agent_order' );	$homeland_agent_orderby = get_option( 'homeland_agent_orderby' );	if( empty( $homeland_all_agents ) ) : 		if( empty( $homeland_about_agents ) ) : ?>			<div class="agent-about-list post-bottom-3em clearfix">				<h3><?php echo homeland_option( 'homeland_team_header', esc_html__( 'Our Team', 'homeland' ) ); ?></h3>				<div class="row">					<?php						$args = array( 							'role' => 'contributor', 							'order' => $homeland_agent_order, 							'orderby' => $homeland_agent_orderby						);					  $homeland_agents = new WP_User_Query( $args );		   					  if ( !empty( $homeland_agents->results ) ) :						  foreach ( $homeland_agents->results as $homeland_user ) :								$homeland_custom_avatar = get_the_author_meta( 'homeland_custom_avatar', $homeland_user->ID ); 								$homeland_agent_link = get_author_posts_url( $homeland_user->ID );								?>				    		<div class="post-col-4 agent-image">				    			<div class="grid cs-style-3">					    				<figure class="pimage">				    					<a href="<?php echo esc_url( $homeland_agent_link ); ?>">				    						<?php if ( !empty( $homeland_custom_avatar ) ) : ?>				    							<img src="<?php echo esc_url( $homeland_custom_avatar ); ?>" srcset="<?php echo esc_url( $homeland_custom_avatar ); ?> 600w" sizes="(max-width: 568px) 100vw, (max-width: 736px) 80vw, (max-width: 768px) 60vw, (max-width: 1024px) 26vw, 245px">					    					<?php else : ?>					    						<?php echo get_avatar( $homeland_user->ID, 600 ); ?>					    					<?php endif; ?>				    					</a>				    					<figcaption>				    						<a href="<?php echo esc_url( $homeland_agent_link ); ?>"><i class="fas fa-link fa-lg"></i></a>				    					</figcaption>				    				</figure>				    			</div>				    			<div class="agent-social">				    				<ul class="clearfix">				    					<?php if( !empty( $homeland_user->homeland_facebook ) ) : ?>				    						<li><a href="<?php echo esc_url( $homeland_user->homeland_facebook ); ?>" target="_blank"><i class="fab fa-facebook-f fa-lg"></i></a></li>				    					<?php endif; ?>				    					<?php if( !empty( $homeland_user->homeland_gplus ) ) : ?>				    						<li><a href="<?php echo esc_url( $homeland_user->homeland_gplus ); ?>" target="_blank"><i class="fab fa-google-plus-g fa-lg"></i></a></li>				    					<?php endif; ?>				    					<?php if( !empty( $homeland_user->homeland_linkedin ) ) : ?>				    						<li><a href="<?php echo esc_url( $homeland_user->homeland_linkedin ); ?>" target="_blank"><i class="fab fa-linkedin-in fa-lg"></i></a></li>				    					<?php endif; ?>				    					<?php if( !empty( $homeland_user->homeland_twitter ) ) : ?>				    						<li><a href="<?php echo esc_url( $homeland_user->homeland_twitter ); ?>" target="_blank"><i class="fab fa-twitter fa-lg"></i></a></li>				    					<?php endif; ?>				    					<?php if( !empty( $homeland_user->user_email ) ) : ?>				    						<li><a href="mailto:<?php echo is_email( $homeland_user->user_email ); ?>" target="_blank"><i class="far fa-envelope fa-lg"></i></a></li>				    					<?php endif; ?>				    				</ul>				    			</div>				    			<div class="textcenter">				    				<h4>					    				<a href="<?php echo esc_url( $homeland_agent_link ); ?>"><?php echo esc_html( $homeland_user->display_name ); ?></a>					    			</h4>					    			<h5><?php echo esc_html( $homeland_user->homeland_designation ); ?></h5>				    			</div>				    		</div><?php						  endforeach;						endif;					?>				</div>			</div><?php		endif;	endif;?>